import React, {useContext} from "react";
import {MyContext} from "../myContext/MyContext";
import Card from "././Card";

function Login() {

	const {
		//state
		firstName,
		lastName,
		email,
		password,
		firstNameError,
		lastNameError,
		emailError,
		passwError,
		successfullAccountMessageVisible,
		//functions
		handleClick,
		handleFirstNameChange,
		handleLastNameChange,
		handleEmailChange,
		handlePasswordChange,
		clearForm
	} = useContext(MyContext);

	return (
		<Card
				bgcolor="warning"
				header="Log In" 
				body={
						<>
							<h5 className="create mb-4">Hello, friend, please log in if not sus</h5>
							<div>
									<div className="error">{firstNameError}</div>
									<div className="error">{lastNameError}</div>
									<div className="error">{emailError}</div>
									<div className="error">{passwError}</div>
							</div>
							<h6 className="name mt-4">Username</h6>
							<input type="input" className="form-control" id="username" placeholder="Your username name is..." value={userName} onChange={handleUserNameChange}/>
							<h6>Password</h6>
							<input type="password" className="form-control" id="password" placeholder="Create a password" value={password} onChange={handlePasswordChange}/>
							<br/>
							<button type="submit" disabled={!userName && !password} id="create" className="submitBtn btn btn-light" onClick={e => handleClick()} >Log in</button>

					</>
				}
		/>
	)
}

export default CreateAccount;
