import React, {useContext, useState} from 'react';
import {MyContext} from "../myContext/MyContext";
import Cookies from "js-cookie";
import Card from "../components/Card";


function AllData() {

	const {
		authenticateWithAPI,
		destinationUser,
		username,
		transferAmount,
		createdAt,
		setError,
	} = useContext(MyContext);

	const [toArray, setToArray] = useState([]);
	const [fromArray, setfromArray] = useState([]);
	const [amountArray, setAmountArray] = useState([]);
	const [createdAtArray, setCreatedAtArray] = useState([]);

	async function getAlltransactions() {

		let userToken = Cookies.get("token");
		if (!userToken) {
			authenticateWithAPI();
			userToken = Cookies.get("token");
		}
		let username = Cookies.get("username");
		if (!username) {
			return
		}
		const API_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:1337";
		const response = await fetch(`${API_URL}/transactions/alltransactions`, {
		  method: "POST",
			headers: userToken && { Authorization: `Bearer ${userToken}` },
		 	body: JSON.stringify({
				username: username
		  }),
		});

    if (!response.ok) {
			console.log(response.statusText)
		  setError(response.statusText);
			} else {
				const responseObj = await response.json();
				const error = responseObj.error;
				const to = responseObj.to;
				const from = responseObj.from;
				const amount = responseObj.amount;
				const createdAt = responseObj.createdAt
				if (error) {
					setError(error);
				}
			}
		};

	return (
		<>
			{toArray.map((to) =>
				<Card
					bgcolor="warning"
					header="All Data"
					body={
						<>  
							<div className="alldata">
								<h6>To: {to}</h6>
							</div>
						</>
					}
				/>
			)}


		</>
	)
};

	
export default AllData;
