import React, {useContext, useEffect, useState} from "react";
import {MyContext} from "../myContext/MyContext";
import Card from "../components/Card";
import Cookies from "js-cookie";

function Invest() {

	const {
		//state:
		balance,
		error,
		//functions:
		setError,
		authenticateWithAPI ,
		getBalance,
	} = useContext(MyContext);

	const [investAmount, setInvestAmount] = useState("");
	const [investError, setInvestError] = useState("");
	const [ investSuccessMessageVisible, setInvestSuccessMessageVisible] = useState(false);
	const [investResult, setInvestResult] = useState();

	useEffect(()=> {
		getBalance();
	}); 

	const validateInvest = () => {
		setInvestError("");
		setInvestSuccessMessageVisible(false);

		if (investAmount == 0.0) {
			investErrorStr = "Amount must be higher than 0.";
			setInvestError(investErrorStr);
			setInvestSuccessMessageVisible(false);
			return false;
		}

		if (investAmount.length >= 7) {
			investErrorStr = "Amount must be lower than that.";
			setInvestError(investErrorStr);
			setInvestSuccessMessageVisible(false);
			return false;
		}

		if (transferAmount > balance) {
			transferErrorStr = "Amount higher than the account balance.";
			setInvestError(investErrorStr);
			setInvestSuccessMessageVisible(false);
			return false;
		} 
		return true;
	};

//INPUT
const investHandleChange = e => {
	e.preventDefault();
	var newValue = e.target.value;
	if (!validateNr(newValue)) { 
		 newValue = newValue.slice(0, -1); 
	}
	setInvestAmount(newValue);
	setInvestSuccessMessageVisible(false);
};

	async function submitInvest() {
		if(!validateInvest()) {
			return;
		}

		let userToken = Cookies.get("token");
			if (!userToken) {
				authenticateWithAPI();
				userToken = Cookies.get("token");
			}

			let username = Cookies.get("username");

			const API_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:1337";
			const response = await fetch(`${API_URL}/transactions/invest`, {
				method: "POST",
				headers: userToken && { Authorization: `Bearer ${userToken}` },
				body: JSON.stringify({
					to: username,
					from: username,
					amount: investAmount
				}),
			});

		setError("");
    if (!response.ok) {
		  setError(response.statusText);
		} else {
			const responseObj = await response.json();
			const error = responseObj.error;
			if (error) {
				setError(error);
			} else {
        setInvestResult(responseObj.result === 1);			
				setInvestSuccessMessageVisible(true);
      }
			getBalance();
		};
  };

	// let clearMsgInvest = () => {
  //   setInvestAmount(0.0);
  //   setInvestSuccessMessageVisible(false);
  // }


	return (
		<Card
				bgcolor="warning"
				header="Invest"
				body= {
					<>  
						{(() => {
							if (investSuccessMessageVisible === true) {
								if (investResult) {
									return (
										<div className="successMessage">
											<h5>Wow!</h5>
											<p>Your investment has doubled the added amount!</p>
											<button type="submit" id="redirectToLambo" className="submitBtn btn btn-light" onClick={() => window.open("https://www.lamborghinistore.com/us/")}>Ok</button>             
										</div>
										);
								} else {
									return (
										<div className="successMessage">
											<h5>Sheesh.</h5>
											<p>Aaaand it's gone!</p>
											<button type="submit" id="redirectToSP" className="submitBtn btn btn-light" onClick={() => window.open("https://media.giphy.com/media/n6Le07BiptVaxfDmWf/giphy.gif/")}>Ok</button>             
										</div>
									);
								}
							}
            })()}


						{(() => {
              if (investError) {
                return (
                  <div className="error">{investError}</div>
                  );
              }
            })()}

						{(() => {
              if (error) {
                return (
                  <div className="error">{error}</div>
                  );
              }
            })()}

						<h6 className="card-text mt-4 mb-5 font-weight-bolder">Your current balance is: ${balance}</h6>

						{/* amount */}
						<h6 className="card-text mt-4 font-weight-bolder">The amount you wish to invest:</h6>
						{/* added value so that state can change */}
						<input type="input" className="form-control" id="investAmount" value={investAmount} onChange={investHandleChange} /><br /><button type="submit" disabled={!investAmount} id="invest" className="submitBtn btn btn-light" onClick={() => submitInvest()}>Invest</button> 
					</>
				}  
		/>
	)
}

export default Invest;
