import React, {useContext, useState, useEffect} from "react";
import {MyContext} from "../myContext/MyContext";
import Card from "../components/Card";
import Cookies from "js-cookie";

function Withdraw() {
  const {
    //state:
    balance,
    error,
    //functions:
    withdrawAmount,
    withdrawHandleChange,
    withdrawError,
    submitWithdrawal,
    showSuccessMessageWithdraw,
    authenticateWithAPI ,
    setError,
    validateWithdraw,
    setWithdrawSuccessMessageVisible,
    getBalance
  } = useContext(MyContext);

  useEffect(()=> {
    getBalance();
  }); 

  const [withdraw, setWithdraw] = useState({
    amount: "" ,
    datetime: "" 
	});

  async function submitWithdraw() {
    if(!validateWithdraw()) {
      return;
    };

    let userToken = Cookies.get("token");
		if (!userToken) {
			authenticateWithAPI();
			userToken = Cookies.get("token");
		}

    const API_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:1337";
		const response = await fetch(`${API_URL}/transactions/withdraw`, {
		  method: "POST",
			headers: userToken && { Authorization: `Bearer ${userToken}` },
		 	body: JSON.stringify({
        amount: withdrawAmount
		  }),
		});

    if (!response.ok) {
			console.log(response.statusText)
		  setError(response.statusText);
		} else {
			console.log("withdraw success");
			const responseObj = await response.json();
			const error = responseObj.error;
			if (error) {
				setError(error);
        setWithdrawSuccessMessageVisible(false)
			} else {
        setWithdrawSuccessMessageVisible(true);
      }
		};
  };

  return (
    <Card
			bgcolor="warning"
			header="Withdraw"
			body={
				<>  
          {(() => {
            if (error) {
              return (
                <div className="error">{error}</div>
                );
            }
          })()}
          
          {(() => {
            if (withdrawError) {
              return (
                <div className="error">{withdrawError}</div>
                );
            }
          })()}

          {showSuccessMessageWithdraw()}

					<h6 className="withdraw-text mt-4 mb-5 font-weight-bolder">Your current balance is: ${balance}</h6>
					<h6 className="withdraw-text mt-4 font-weight-bolder">The amount you wish to withdraw:</h6>
					<input type="input" className="form-control" id="email" onChange={withdrawHandleChange}/>
					<br/>
					<button type="submit" disabled={!withdrawAmount} id="withdraw" className="submitBtn btn btn-light" onClick={() => submitWithdraw()}>Withdraw</button>
				</>
      }
    />
  )
}

export default Withdraw;
