import React, {useContext} from "react";
import {MyContext} from "../myContext/MyContext";
import Card from "././Card";
import CreateAccount from "./CreateAccount";

function Login() {

	const {
		//state
    userName,
		password,
		userNameError,
		passwError,
		successfullAccountMessageVisible,
		//functions
    handleClickLogin,
		handleUserNameChange,
		handlePasswordChange,
    showSuccessMessageLogin,
	} = useContext(MyContext);

	return (
		<Card
			bgcolor="warning"
			header="Account log in"
			body={
				<>
				{(() => {
						if (userNameError) {
							return (
								<div className="error">{userNameError}</div>
								);
							} else { 
								if (passwError) {
									return (
										<div className="error">{passwError}</div>
									);
							} else {
								return; 
							}	
					}})
				()};

					{/* <div className="messageContainer"> */}
						{/* <div className="error">{userNameError}</div> */}
						{/* {userNameError} */}
					{/* </div> */}

					{/* <div className="messageContainer"> */}
						{/* <div className="error">{passwError}</div> */}
						{/* {passwError} */}
					{/* </div> */}


					{/* I probably don't need this container? */}
					{/* <div className="messageContainer mt-5"> */}
          	{showSuccessMessageLogin()}
        	{/* </div>	 */}

					<h6 className="login-text mt-5">Username</h6>
					<input type="input" className="form-control" id="username" placeholder="@..." value={userName} onChange={handleUserNameChange}/>
					<h6 className="login-text mt-4">Password</h6>
					<input type="password" className="form-control" id="password" placeholder="Your password is..." value={password} onChange={handlePasswordChange}/>
					<br/>
					<button type="submit" disabled={!userName && !password} id="login" className="submitBtn btn btn-light" onClick={e => handleClickLogin()} >Log in</button>
						<br/>
					<h6 className="account-text font-weight-bold mt-4">Don't have an account yet?</h6>
						<br/>
						<button type="submit" id="create" className="submitBtn btn btn-light" onClick={e =>  window.location='./CreateAccount'} >Open Account</button>


				</>
				}
		/>
	)
}

export default Login;
