import React, {useContext, useEffect} from "react";
import {MyContext} from "../myContext/MyContext";
import Card from "../components/Card";
import Cookies from "js-cookie";

function Invest() {

	const {
		//state:
		balance,
		error,
		investSuccessMessageVisible,
		investAmount,
		//functions:
		validateInvest,
		setError,
		authenticateWithAPI ,
		getBalance,
		investHandleChange,
		setInvestSuccessMessageVisible
	} = useContext(MyContext);

	useEffect(()=> {
		getBalance();
	}); 

	//this isn't what i want but i'll leave it like this for now
	// const redirectToLambo = () => {
	// 	return window.location.href = ("https://www.lamborghinistore.com/us/", "_blank"); 
  // }

	const redirectToSouthPark = () => {
		return window.location.href = ("/https://media.giphy.com/media/n6Le07BiptVaxfDmWf/giphy.gif", "_blank");
	}


	async function submitInvest() {
		if(!validateInvest) {
			return;
		}

		let userToken = Cookies.get("token");
			if (!userToken) {
				authenticateWithAPI();
				userToken = Cookies.get("token");
			}

			let username = Cookies.get("username");

			const API_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:1337";
			const response = await fetch(`${API_URL}/transactions/invest`, {
				method: "POST",
				headers: userToken && { Authorization: `Bearer ${userToken}` },
				body: JSON.stringify({
					to: username,
					from: username,
					amount: investAmount
				}),
			});

    if (!response.ok) {
			console.log(response.statusText)
		  setError(response.statusText);
		} else {
			console.log(response)
			const responseObj = await response.json();
			const error = responseObj.error;
			if (error) {
				setError(error);
        setInvestSuccessMessageVisible(false)
			} else {
        setInvestSuccessMessageVisible(true);
				clearMsgInvest();
      }
		};
  };

	let clearMsgInvest = () => {
    setInvestAmount(0.0);
    setInvestSuccessMessageVisible(false);
  }

	const successfulInvestment = () => {
		window.open("https://www.lamborghinistore.com/us/");
		clearMsgInvest();
	}

	return (
		<Card
				bgcolor="warning"
				header="Invest"
				body= {
					<>  
						{(() => {
							if (investSuccessMessageVisible === true) {
								return (
									<div className="successMessage">
										<h5>Wow!</h5>
										<p>Your investment has doubled the added amount!</p>
										<button type="submit" id="redirectToLambo" className="submitBtn btn btn-light" onClick={() => successfulInvestment()}>Ok</button>             
									</div>
								);
							}
						})()}

						{(() => {
							if (investAmount == 0) {
								return (
									<div className="successMessage">
										<h5>Sheesh.</h5>
										<p>Your investment lost all of your money.</p>
										<button type="submit" id="redirectToSP" className="submitBtn btn btn-light" onClick={() => redirectToSouthPark()}>Ok</button>             
									</div>
								);
							}
						})()}

						<h6 className="card-text mt-4 mb-5 font-weight-bolder">Your current balance is: ${balance}</h6>

						{/* amount */}
						<h6 className="card-text mt-4 font-weight-bolder">The amount you wish to invest:</h6>
						{/* added value so that state can change */}
						<input type="input" className="form-control" id="investAmount" value={investAmount} onChange={investHandleChange}/>
						<br/>  
						<button type="submit" disabled={!investAmount} id ="transfer" className="submitBtn btn btn-light" onClick={() => submitInvest()}>Invest</button>            
					</>
				}  
		/>
	)
}

export default Invest;
