import React, {useContext, useState} from "react";
import {useHistory} from "react-router-dom";
import {MyContext} from "../myContext/MyContext";
import Card from "../components/Card";
import Cookies from "js-cookie";

function Login() {

	const {
		//state
		firstName,
    userName,
		password,
		userNameError,
		passwError,
		error,
		result,
		loggedInUserName,
		//functions
    handleClickLogin,
		handleUserNameChange,
		handlePasswordChange,
    showSuccessMessageLogin,
		validate,
		clearForm,
		authenticateWithAPI,
		setError,
		setResult,
		clearLoginForm,
		setLoggedInUserName,
		clearMsgLogin

	} = useContext(MyContext);

	/*
	The useHistory hook allows us to access React Router's history object.

	Through the history object, we can access and manipulate the current state of the browser history.

	All we need to do is to call the useHistory hook inside a functional component.

	We can use this object to redirect the user to another page by calling history.push('/example-route').
	*/

	//redirects me to Create Account when clicking "Open Account" in Login 
	const history = useHistory();
  	const redirect = () => {
    // history.push('/createAccount');
		// using the href from Navigation, not the "path" I was thinking I need: {id:"createAccount", text: "Open Acount", tooltipText: "Open your BetterBank速 account", href:"#/createAccount"}
		// I don't want to be able to go back in the browser:
		history.replace('/createAccount');
  	}

	const [user, setUser] = useState({
		username: "",
		password: ""
		});

	async function loginUser() {

		let userToken = Cookies.get("token");
		if (!userToken) {
			authenticateWithAPI();
			userToken = Cookies.get("token");
		}

		const API_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:1337";
		const response = await fetch(`${API_URL}/newusers/login`, {
		  method: "POST",
			headers: userToken && { Authorization: `Bearer ${userToken}` },
		 	body: JSON.stringify({
				firstname: firstName,
				username: userName,
				password: password,
		  }),
		});

		if (!response.ok) {
		 	setResult(response.statusText);
		} else {
			const responseObj = await response.json();
			console.log("Response: " + JSON.stringify(responseObj));
			const result = responseObj.result;
			//if I don't set state, it doesn't appear on the frontend
			setResult(result);

			const redirect = () => {
			// history.push('/createAccount');
			// using the href from Navigation, not the "path" I was thinking I need: {id:"createAccount", text: "Open Acount", tooltipText: "Open your BetterBank速 account", href:"#/createAccount"}
			// I don't want to be able to go back in the browser:
			history.replace('alldata');
		}
			// result === "success" ? 	setLoggedInUserName(true) : setLoggedInUserName(false);
		}
	}	

	return (
		<Card
			bgcolor="warning"
			header="Account log in"
			body={
				<>
				{/* {(() => {
					console.log("hey result: " + result)
						if (loggedInUserName) {
							{clearLoginForm()}
							return (
								<div className="successMessage">
									<h5>Awesome, {firstName}!</h5>
									<p>You are logged in.</p>
									<p>Get started by using the navigation bar above.</p>
									<button type="submit" id="clearLogin" className="submitBtn btn btn-light" onClick={() => clearMsgLogin()}>Ok</button>             
								</div>
							)} 
					})()} */}

					{(() => {
							if (result) {
								return (
									<div className="error">{result}</div>
									);
								} 
							})()}




					<h6 className="login-text mt-5">Username</h6>
					<input type="input" className="form-control" id="username" placeholder="@" value={userName} onChange={handleUserNameChange}/>
					<h6 className="login-text mt-4">Password</h6>
					<input type="password" className="form-control" id="password" placeholder="**********" value={password} onChange={handlePasswordChange}/>
					<br/>
					<button type="submit" disabled={!userName && !password} id="login" className="submitBtn btn btn-light" onClick={e => loginUser()} >Log in</button>
						<br/>
					<h6 className="account-text font-weight-bold mt-4">Don't have an account yet?</h6>
					<br/>
					<button type="submit" id="create" className="submitBtn btn btn-light" onClick={redirect} >Open Account</button>
				</>
				}
		/>
	)
}

export default Login;
