import React, {useContext} from "react";
import {MyContext} from "../myContext/MyContext";
import Card from "././Card";

function Login() {

	const {
		//state
        userName,
		password,
		userNameNameError,
		passwError,
		successfullAccountMessageVisible,
		//functions
		handleClick,
		handleUserNameChange,
		handlePasswordChange,
		clearForm
	} = useContext(MyContext);

	return (
		<Card
				bgcolor="warning"
				header="Customer Log In" 
				body={
						<>
							<h5 className="create mb-4">Hello, friend, please log in if not sus</h5>
							<div>
									<div className="error">{userNameNameError}</div>
									<div className="error">{passwError}</div>
							</div>
							<h6 className="name mt-4">Username</h6>
							<input type="input" className="form-control" id="username" placeholder="@..." value={userName} onChange={handleUserNameChange}/>

                            <h6 className="name mt-4">Password</h6>
							<input type="password" className="form-control" id="password" placeholder="Your password is..." value={password} onChange={handlePasswordChange}/>
							<br/>
							<button type="submit" disabled={!userName && !password} id="login" className="submitBtn btn btn-light" onClick={e => handleClick()} >Log in</button>

					</>
				}
		/>
	)
}

export default Login;
