import React, {useContext, useState} from "react";
import {MyContext} from "../myContext/MyContext";
import Card from "../components/Card";
import Cookies from "js-cookie";

function Deposit() {

  const {
    //state:
    depositAmount,
    balance,
    depositError,
    userName,
    error,
    loggedInUserName,
    dateTime,
    //functions:
    depositHandleChange,
    submitDeposit,
    showSuccessMessageDeposit,
    setError,
    clearForm,
    authenticateWithAPI,
    
  } = useContext(MyContext);

	const [deposit, setDeposit] = useState({
		to: "",
    from: "",
    amount: "" ,
    datetime: "" 
	});

  async function depositBackend() {
    if(!validateDeposit()) {
    }
	
		let userToken = Cookies.get("token");
		if (!userToken) {
			authenticateWithAPI();
			userToken = Cookies.get("token");
		}
		
		const API_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:1337";
		const response = await fetch(`${API_URL}/transaction/deposit`, {
		  method: "POST",
			headers: userToken && { Authorization: `Bearer ${userToken}` },
		 	body: JSON.stringify({
				to: userName,
				from: loggedInUserName,
        amount: depositAmount ,
        datetime: dateTime 
		  }),
		});


    if (!response.ok) {
			console.log(response.statusText)
		  setError(response.statusText);
		} else {
			console.log("Success");
			const responseObj = await response.json();
			const error = responseObj.error;
			if (error) {
				setError(error);
			}
		}
	}	

  return (
    <Card
        bgcolor="warning"
        header="Deposit"
        body= {
          <>  

            {(() => {
							if (error) {
								return (
									<div className="error">{error}</div>
									);
								} 
							})()}
          	{(() => {
              if (depositError) {
                return (
                  <div className="error">{depositError}</div>
                  );
              }
            })()}


            {showSuccessMessageDeposit()}
          
						{/* <h5 className="deposit mb-4">Skip extra trips to the bank and deposit your money online!</h5> */}
						<h6 className="deposit-text mt-4 mb-5 font-weight-bolder">Your current balance is: ${balance}</h6>
						<h6 className="deposit-text mt-4 font-weight-bolder">The amount you wish to deposit:</h6>
						<input type="input" className="form-control" id="depositAmount" placeholder="Amount" onChange={depositHandleChange}/>
						{/* <div>
							<div className="error mb-3">{depositError}</div>
						</div> */}
						<br/>                                                                              
						<button type="submit" disabled={!depositAmount} id ="deposit" className="submitBtn btn btn-light" onClick={() => depositBackend(depositAmount)}>Deposit</button>            
          </>
        }  
    />
  )
}

export default Deposit;
