import React, {useContext } from 'react';
import {MyContext} from "../myContext/MyContext";
import Card from "../components/Card";

//useQuery is a hook that we can use to send queries to the GQL server. it returns loading, error, and data
//gql is a function that's used by apollo to convert a query string into a format it can use
import {useQuery, gql} from "@apollo/client";

const USERS = gql`
	query GetUsers {
		newuser {
			username,
			firstname,
			lastname,
			email
		}
	}
`

function AllData() {
	//destructure loading, error, and data that we get from the useQuery function:
	//it's going to make the query for us to Strapi and Strapi is going to send us back these 3 values
	//data is an object
	const {loading, error, data} = useQuery(USERS)

	if (loading) {
		return <p>Loading...</p>
	}
	if (error) {
		return <p>Error.</p>
	}

	// const {users, depositArr, withdrawArr} = useContext(MyContext);
	// let currentTime = new Date().toLocaleString();
	return (
		// we need Fragment, otherwise it returns an object
		<>  
			{data.newuser.map((user) => 
				<>
					<Card
						bgcolor="info"
						header="Your Accounts"
						body={
								<>  
									<h5 className="balance mb-3">Opened accounts:</h5>
									<div className="alldata">
										{/* <h6>Creation date: {currentTime}</h6> */}
										<h6>Name: {user.firstname} {user.lastname}</h6>
										<h6>Email address: {user.email}</h6>
										{/* <h6>Password: {user.password}</h6>									 */}
									</div>
								</>
							}
					/>
				</>
			)}

			{/* {depositArr.map((amount) => 
				<>
					<Card
						bgcolor="info"
						header="Your Transactions"
						body={
								<>  
									<h5 className="balance mb-3">Deposit:</h5>
									<div className="alldata">
										<h6>Creation date: {currentTime}</h6>
										<h6>Amount: ${amount.depositAmount}</h6>
										<br/>
										<br/>
									</div>
								</>
							}
						/>
					</>
			)} */}

		{/* {withdrawArr.map((value) => 
			<>
				<Card 
					bgcolor="info"
					header="Your Transactions"
					body={
							<>  
								<h5 className="balance mb-3">Withdraw:</h5>
								<div className="alldata">
									<h6>Creation date: {currentTime}</h6>
									<h6>Amount: ${value.withdrawAmount}</h6>
									<br/>
									<br/>										
								</div>
							</>
						}
					/>
				</>
			)} */}
		</>
	) //return
}; //function

	
export default AllData;
