import React, {useContext, useState, useEffect} from "react";
import {MyContext} from "../myContext/MyContext";
import Card from "../components/Card";
import Cookies from "js-cookie";

function Deposit() {

  const {
    //state:
    depositAmount,
    balance,
    depositError,
    error,
    //functions:
    depositHandleChange,
    validateDeposit,
    depositSuccessMessageVisible,
    setError,
    authenticateWithAPI ,
    setBalance,
    setDepositSuccessMessageVisible,
    setDepositAmount
  } = useContext(MyContext);

  useEffect(()=> {
    getBalance();
  }); 

	const [deposit, setDeposit] = useState({
    amount: "" ,
    datetime: "" 
	});

  async function getBalance() {
    let userToken = Cookies.get("token");
		if (!userToken) {
			authenticateWithAPI();
			userToken = Cookies.get("token");
		}

		const API_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:1337";
    const balanceResponse = await fetch(`${API_URL}/transactions/balance`, {
      method: "POST",
      headers: userToken && { Authorization: `Bearer ${userToken}` },
      body: {}
    });

    if (!balanceResponse.ok) {
      console.log(balanceResponse.statusText)
      setError(balanceResponse.statusText);
    } else {
      console.log("Success");
      const balanceResponseObj = await balanceResponse.json();
      const balanceError = balanceResponseObj.error;
      if (!balanceError) {
        setBalance(balanceResponseObj.balance);
     }
    }
    return balance;
  }

  async function submitDeposit() {
    if(!validateDeposit()) {
      return;
    }
	
		let userToken = Cookies.get("token");
		if (!userToken) {
			authenticateWithAPI();
			userToken = Cookies.get("token");
		}
		
		const API_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:1337";
		const response = await fetch(`${API_URL}/transactions/deposit`, {
		  method: "POST",
			headers: userToken && { Authorization: `Bearer ${userToken}` },
		 	body: JSON.stringify({
        amount: depositAmount
		  }),
		});

    if (!response.ok) {
			console.log(response.statusText)
		  setError(response.statusText);
		} else {
			console.log("Success");
			const responseObj = await response.json();
			const error = responseObj.error;
			if (error) {
				setError(error);
        setDepositSuccessMessageVisible(false)
			} else {
        setBalance(responseObj.newbalance);
        setDepositSuccessMessageVisible(true);
      }
		};
  };


    let clearMsgDeposit = () => {
      setDepositAmount(0.0);
      setDepositSuccessMessageVisible(false);
    }

  return (
    <Card
        bgcolor="warning"
        header="Deposit"
        body= {
          <>  

            {(() => {
							if (error) {
								return (
									<div className="error">{error}</div>
									);
								} 
							})()}

          	{(() => {
              if (depositError) {
                return (
                  <div className="error">{depositError}</div>
                  );
              }
            })()}

            {(() => {
							if (depositSuccessMessageVisible === true) {
								return (
                  <div className="successMessage">
                    <h5>Awesome!</h5>
                    <p>Your deposit was received!</p>
                    <button type="submit" id="clearDep" className="submitBtn btn btn-light" onClick={() => clearMsgDeposit()}>Ok</button>             
                  </div>
				        );
							}
						})()}

          	<h6 className="deposit-text mt-4 mb-5 font-weight-bolder">Your current balance is: {balance}</h6>
						<h6 className="deposit-text mt-4 font-weight-bolder">The amount you wish to deposit:</h6>
						<input type="input" className="form-control" id="depositAmount" value={depositAmount} onChange={depositHandleChange}/>
						<br/>                                                                              
						<button type="submit" disabled={!depositAmount} id ="deposit" className="submitBtn btn btn-light" onClick={() => submitDeposit()}>Deposit</button>            
          </>
        }  
    />
  )
}

export default Deposit;
