import React, {useContext} from "react";
import {MyContext} from "../myContext/MyContext";
import Card from "../components/Card";

function Deposit() {

  const {
    //state:
    depositAmount,
    balance,
    depositError,
    //functions:
    depositHandleChange,
    submitDeposit,
    showSuccessMessageDeposit
    
  } = useContext(MyContext);


  async function deposit() {
	
		let userToken = Cookies.get("token");
		if (!userToken) {
			authenticateWithAPI();
			userToken = Cookies.get("token");
		}
		
		const API_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:1337";
		const response = await fetch(`${API_URL}/transaction/deposit`, {
		  method: "POST",
			headers: userToken && { Authorization: `Bearer ${userToken}` },
		 	body: JSON.stringify({
				to: "",
				from: "",
        amount: "" ,
        datetime: "" 
		  }),
		});


		if (!response.ok) {
		 	setResult(response.statusText);
		} else {
			const responseObj = await response.json();
			console.log("Response: " + JSON.stringify(responseObj));
			const result = responseObj.result;
			//if I don't set state, it doesn't appear on the frontend
			setResult(result);
			if (result === "success") { 
				setResult("");
				setRedirectToLogin(false)
				setLoggedInUserName(responseObj.username);
				Cookies.set("username", responseObj.username);
				clearLoginForm();
				redirectToData();
			} 
			if (result !== "success") { 
				setRedirectToLogin(false);
			}
		}
	}	

  return (
    <Card
        bgcolor="warning"
        header="Deposit"
        body= {
          <>  
          	{(() => {
              if (depositError) {
                return (
                  <div className="error">{depositError}</div>
                  );
              }
            })()}

            {(() => {
							if (error) {
								return (
									<div className="error">{error}</div>
									);
								} 
							})()}

            {showSuccessMessageDeposit()}
          
						{/* <h5 className="deposit mb-4">Skip extra trips to the bank and deposit your money online!</h5> */}
						<h6 className="deposit-text mt-4 mb-5 font-weight-bolder">Your current balance is: ${balance}</h6>
						<h6 className="deposit-text mt-4 font-weight-bolder">The amount you wish to deposit:</h6>
						<input type="input" className="form-control" id="depositAmount" placeholder="Amount" onChange={depositHandleChange}/>
						{/* <div>
							<div className="error mb-3">{depositError}</div>
						</div> */}
						<br/>                                                                              
						<button type="submit" disabled={!depositAmount} id ="deposit" className="submitBtn btn btn-light" onClick={() => submitDeposit(depositAmount)}>Deposit</button>            
          </>
        }  
    />
  )
}

export default Deposit;
